Copy infolog messages:
    str getInfologMessages(int _fromLine = 1, int _toLine = infolog.num())
    {
        SysInfologEnumerator enumerator;
        SysInfologMessageStruct msgStruct;
        //Exception exception;
        str allMessages = '';

        enumerator = SysInfologEnumerator::newData(infolog.copy(_fromLine, _toLine));
        while (enumerator.moveNext())
        {
            msgStruct = new SysInfologMessageStruct(enumerator.currentMessage());
            //exception = enumerator.currentException();
            allMessages = ((allMessages) ? "\n" : '') + msgStruct.message();
        }
        return allMessages;
    }
